import androidx.compose.runtime.Composable
import androidx.compose.runtime.LaunchedEffect
import androidx.compose.runtime.getValue
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import androidx.lifecycle.viewmodel.compose.viewModel
import com.example.diaensho.viewmodel.HomeViewModel
import androidx.lifecycle.compose.collectAsStateWithLifecycle
import com.example.diaensho.data.db.entity.AppUsageStatEntity
import com.example.diaensho.data.db.entity.DiaryEntryEntity
import java.time.LocalDate
import java.time.format.DateTimeFormatter
import java.util.concurrent.TimeUnit

@Composable
fun HomeScreen(
    homeViewModel: HomeViewModel = viewModel(),
    selectedDate: LocalDate,
    onDateChange: (LocalDate) -> Unit,
    onSearchClick: () -> Unit
) {
    val entries by homeViewModel.entries.collectAsStateWithLifecycle()
    val appUsageStats by homeViewModel.appUsageStats.collectAsStateWithLifecycle()

    // Update ViewModel when date changes
    LaunchedEffect(selectedDate) {
        homeViewModel.loadDataForDate(selectedDate)
    }

    Column(modifier = Modifier.fillMaxSize().padding(16.dp)) {
        // Date selector
        DateSelector(
            selectedDate = selectedDate,
            onPreviousDay = { onDateChange(selectedDate.minusDays(1)) },
            onNextDay = { onDateChange(selectedDate.plusDays(1)) }
        )

        Spacer(modifier = Modifier.height(16.dp))

        // App usage stats summary
        AppUsageSection(appUsageStats)

        Spacer(modifier = Modifier.height(16.dp))

        // Diary entries
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                text = "Diary Entries",
                style = MaterialTheme.typography.titleMedium
            )
            Text(
                text = "${entries.size} entries",
                style = MaterialTheme.typography.bodySmall,
                color = MaterialTheme.colorScheme.onSurfaceVariant
            )
        }

        Spacer(modifier = Modifier.height(8.dp))

        if (entries.isEmpty()) {
            // Show empty state
            Card(
                modifier = Modifier.fillMaxWidth(),
                colors = CardDefaults.cardColors(
                    containerColor = MaterialTheme.colorScheme.surfaceVariant
                )
            ) {
                Column(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(32.dp),
                    horizontalAlignment = Alignment.CenterHorizontally
                ) {
                    Text(
                        text = "No diary entries for this day",
                        style = MaterialTheme.typography.bodyLarge,
                        color = MaterialTheme.colorScheme.onSurfaceVariant
                    )
                    Spacer(modifier = Modifier.height(8.dp))
                    Text(
                        text = "Say 'computer' followed by your thoughts to create an entry",
                        style = MaterialTheme.typography.bodySmall,
                        color = MaterialTheme.colorScheme.onSurfaceVariant,
                        textAlign = androidx.compose.ui.text.style.TextAlign.Center
                    )
                }
            }
        } else {
            LazyColumn(
                modifier = Modifier.weight(1f),
                verticalArrangement = Arrangement.spacedBy(8.dp)
            ) {
                items(entries, key = { it.id }) { entry ->
                    DiaryEntryCard(entry)
                }
            }
        }

        // Search button
        Button(
            onClick = onSearchClick,
            modifier = Modifier.fillMaxWidth().padding(vertical = 8.dp)
        ) {
            Text("Search Entries")
        }
    }
}

@Composable
private fun DateSelector(
    selectedDate: LocalDate,
    onPreviousDay: () -> Unit,
    onNextDay: () -> Unit
) {
    Row(
        modifier = Modifier.fillMaxWidth(),
        horizontalArrangement = Arrangement.SpaceBetween,
        verticalAlignment = Alignment.CenterVertically
    ) {
        IconButton(onClick = onPreviousDay) {
            Icon(Icons.AutoMirrored.Default.KeyboardArrowLeft, "Previous day")
        }

        Text(
            text = selectedDate.format(DateTimeFormatter.ofPattern("MMMM d, yyyy")),
            style = MaterialTheme.typography.titleMedium
        )

        IconButton(onClick = onNextDay) {
            Icon(Icons.AutoMirrored.Default.KeyboardArrowRight, "Next day")
        }
    }
}

@Composable
private fun AppUsageSection(stats: List<AppUsageStatEntity>) {
    Card(
        modifier = Modifier.fillMaxWidth(),
    ) {
        Column(
            modifier = Modifier.padding(16.dp)
        ) {
            Text(
                text = "App Usage Summary",
                style = MaterialTheme.typography.titleMedium
            )
            Spacer(modifier = Modifier.height(8.dp))
            stats.take(5).forEach { stat ->
                Row(
                    modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.SpaceBetween
                ) {
                    Text(stat.packageName.split(".").last())
                    Text(formatDuration(stat.totalTimeInForeground))
                }
            }
        }
    }
}

@Composable
private fun DiaryEntryCard(entry: DiaryEntryEntity) {
    Card(
        modifier = Modifier.fillMaxWidth(),
    ) {
        Column(
            modifier = Modifier.padding(16.dp)
        ) {
            Text(
                text = entry.text,
                style = MaterialTheme.typography.bodyMedium
            )
            Spacer(modifier = Modifier.height(4.dp))
            Text(
                text = entry.timestamp.format(DateTimeFormatter.ofPattern("HH:mm")),
                style = MaterialTheme.typography.bodySmall
            )
        }
    }
}

private fun formatDuration(millis: Long): String {
    val hours = TimeUnit.MILLISECONDS.toHours(millis)
    val minutes = TimeUnit.MILLISECONDS.toMinutes(millis) % 60
    return when {
        hours > 0 -> "${hours}h ${minutes}m"
        minutes > 0 -> "${minutes}m"
        else -> "< 1m"
    }
}

package com.example.diaensho.ui.screen

import androidx.compose.foundation.layout.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.platform.LocalContext
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.unit.dp
import androidx.lifecycle.viewmodel.compose.viewModel
import com.example.diaensho.util.PowerManagerHelper
import com.example.diaensho.viewmodel.OnboardingViewModel
import androidx.lifecycle.compose.collectAsStateWithLifecycle

@Composable
fun OnboardingScreen(
    onboardingViewModel: OnboardingViewModel = viewModel(),
    onPermissionsGranted: () -> Unit,
    onRequestMicPermission: () -> Unit,
    onRequestUsageStatsPermission: () -> Unit
) {
    val uiState by onboardingViewModel.uiState.collectAsStateWithLifecycle()
    val context = LocalContext.current

    Column(
        modifier = Modifier
            .fillMaxSize()
            .padding(16.dp),
        verticalArrangement = Arrangement.spacedBy(16.dp, Alignment.CenterVertically),
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        Text(
            text = "Welcome to Cogniscribe!",
            style = MaterialTheme.typography.headlineMedium,
            textAlign = TextAlign.Center
        )

        Text(
            text = "Please grant the following permissions to enable voice diary entries:",
            style = MaterialTheme.typography.bodyLarge,
            textAlign = TextAlign.Center
        )

        Spacer(modifier = Modifier.height(24.dp))

        // Microphone Permission
        PermissionButton(
            text = if (uiState.microphonePermissionGranted) "âœ“ Microphone Granted" else "Grant Microphone Permission",
            onClick = onRequestMicPermission,
            enabled = !uiState.microphonePermissionGranted,
            description = "Required for voice recognition"
        )

        // Usage Stats Permission
        PermissionButton(
            text = if (uiState.usageStatsPermissionGranted) "âœ“ Usage Stats Granted" else "Grant Usage Stats Permission",
            onClick = onRequestUsageStatsPermission,
            enabled = !uiState.usageStatsPermissionGranted,
            description = "Required for app usage tracking"
        )

        // Battery Optimization Permission
        PermissionButton(
            text = if (uiState.batteryOptimizationDisabled) "âœ“ Battery Optimization Disabled" else "Disable Battery Optimization",
            onClick = {
                context.startActivity(onboardingViewModel.getBatteryOptimizationIntent())
            },
            enabled = !uiState.batteryOptimizationDisabled,
            description = "Required for reliable voice detection"
        )

        Spacer(modifier = Modifier.height(32.dp))

        Button(
            onClick = onPermissionsGranted,
            enabled = uiState.allPermissionsGranted,
            modifier = Modifier.fillMaxWidth()
        ) {
            Text("Continue")
        }

        if (!uiState.allPermissionsGranted) {
            Text(
                text = "Please grant all permissions to continue",
                style = MaterialTheme.typography.bodySmall,
                color = MaterialTheme.colorScheme.error
            )
        }
    }
}

@Composable
private fun PermissionButton(
    text: String,
    onClick: () -> Unit,
    enabled: Boolean,
    description: String
) {
    Column(
        horizontalAlignment = Alignment.CenterHorizontally,
        modifier = Modifier.fillMaxWidth()
    ) {
        Button(
            onClick = onClick,
            enabled = enabled,
            modifier = Modifier.fillMaxWidth()
        ) {
            Text(text)
        }
        if (enabled) {
            Text(
                text = description,
                style = MaterialTheme.typography.bodySmall,
                color = MaterialTheme.colorScheme.onSurfaceVariant
            )
        }
    }
}

